import {
  withAuth
} from "./chunk-RN3IUPE5.mjs";
import {
  BASE_PATH
} from "./chunk-KSG3E4Q2.mjs";

// src/libraries/backend/routes/productRoutes.ts
import { addRoute } from "rou3";
var listProductsHandler = withAuth({
  fn: async ({
    autumn,
    customer_id
  }) => {
    return await autumn.products.list({
      customer_id
    });
  },
  requireCustomer: false
});
var addProductRoutes = async (router) => {
  addRoute(router, "GET", `${BASE_PATH}/products`, {
    handler: listProductsHandler
  });
};

export {
  addProductRoutes
};
